const removeFromArray = function(values, remove) { 
        let localArray
    for (let i = 0; i < values.length; i++) {
            if(values[i] === remove) {
                    values.splice();
                    localArray = values;
            }
            return localArray;
    }   
 return localArray
};

// Do not edit below this line
module.exports = removeFromArray;
